SELECT TF.F_FDCODE, TF.E_EVTCODE, TE.E_NTTILTE, TE.E_STARTDATE, TE.E_ENDDATE, SYSDATE AS TODAY, TF.FC_1ST, (SELECT TFC.FC_CTGNAME FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TFC.FC_2ND = 0 AND TFC.FC_3RD = 0) AS FCNAME_1ST, TF.FC_2ND, (SELECT TFC.FC_CTGNAME FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TF.FC_2ND = TFC.FC_2ND AND TFC.FC_3RD = 0) AS FCNAME_2ND, TF.FC_3RD, (SELECT TFC.FC_CTGNAME FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TF.FC_2ND = TFC.FC_2ND AND TF.FC_3RD = TFC.FC_3RD) AS FCNAME_3RD, (SELECT TFC.FC_ISBLOCK FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TF.FC_2ND = TFC.FC_2ND AND TF.FC_3RD = TFC.FC_3RD) AS FC_ISBLOCK, TF.F_FOODNAME, TF.F_PRICE, TF.F_EXPLAN, TF.F_IMGNAME, TF.F_THUMNAME, TF.F_ISBLOCK, TF.MS_CODE FROM TR_FOOD TF LEFT OUTER JOIN TR_EVENT TE ON TF.E_EVTCODE = TE.E_EVTCODE
TF.FC_1ST = #{fc_1st}
AND TF.FC_2ND = #{fc_2nd}
AND TF.FC_3RD = #{fc_3rd}

SELECT * FROM (
SELECT F_FDCODE, E_EVTCODE, E_NTTILTE, E_STARTDATE, E_ENDDATE, TODAY, FC_1ST, FCNAME_1ST, FC_2ND, FCNAME_2ND, FC_3RD, FCNAME_3RD, FC_ISBLOCK, F_FOODNAME, F_PRICE, F_EXPLAN, F_IMGNAME, F_THUMNAME, F_ISBLOCK, MS_CODE, rownum rn FROM ( SELECT TF.F_FDCODE, TF.E_EVTCODE, TE.E_NTTILTE, TE.E_STARTDATE, TE.E_ENDDATE, SYSDATE AS TODAY, TF.FC_1ST, (SELECT TFC.FC_CTGNAME FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TFC.FC_2ND = 0 AND TFC.FC_3RD = 0) AS FCNAME_1ST, TF.FC_2ND, (SELECT TFC.FC_CTGNAME FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TF.FC_2ND = TFC.FC_2ND AND TFC.FC_3RD = 0) AS FCNAME_2ND, TF.FC_3RD, (SELECT TFC.FC_CTGNAME FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TF.FC_2ND = TFC.FC_2ND AND TF.FC_3RD = TFC.FC_3RD) AS FCNAME_3RD, (SELECT TFC.FC_ISBLOCK FROM TR_FOODCODE TFC WHERE TF.FC_1ST = TFC.FC_1ST AND TF.FC_2ND = TFC.FC_2ND AND TF.FC_3RD = TFC.FC_3RD) AS FC_ISBLOCK, TF.F_FOODNAME, TF.F_PRICE, TF.F_EXPLAN, TF.F_IMGNAME, TF.F_THUMNAME, TF.F_ISBLOCK, TF.MS_CODE FROM TR_FOOD TF LEFT OUTER JOIN TR_EVENT TE ON TF.E_EVTCODE = TE.E_EVTCODE
TF.FC_1ST = #{fc_1st}
AND TF.FC_2ND = #{fc_2nd}
AND TF.FC_3RD = #{fc_3rd}
AND TF.F_FOODNAME LIKE '%' || #{word } || '%'
ORDER BY TF.F_FOODNAME ASC, TF.FC_1ST ASC, TF.FC_2ND ASC, TF.FC_3RD ASC ORDER BY TF.F_FOODNAME DESC, TF.FC_1ST ASC, TF.FC_2ND ASC, TF.FC_3RD ASC
ORDER BY TF.F_PRICE ASC, TF.FC_1ST ASC, TF.FC_2ND ASC, TF.FC_3RD ASC ORDER BY TF.F_PRICE DESC, TF.FC_1ST ASC, TF.FC_2ND ASC, TF.FC_3RD ASC
ORDER BY TF.F_FDCODE DESC, TF.FC_1ST ASC, TF.FC_2ND ASC, TF.FC_3RD ASC
) )
rn between #{startRow} and #{endRow}

